import type { DefaultSharedModuleContext, LangiumServices, LangiumSharedServices, Module, PartialLangiumServices } from 'langium';
import { createDefaultModule, createDefaultSharedModule, inject } from 'langium';
import { ArduinoMlBGeneratedModule, ArduinoMlBGeneratedSharedModule } from './generated/module.js';
import { ArduinoMlBValidator, registerValidationChecks } from './arduino-ml-b-validator.js';

/**
 * Declaration of custom services - add your own service classes here.
 */
export type ArduinoMlBAddedServices = {
    validation: {
        ArduinoMlBValidator: ArduinoMlBValidator
    }
}

/**
 * Union of Langium default services and your custom services - use this as constructor parameter
 * of custom service classes.
 */
export type ArduinoMlBServices = LangiumServices & ArduinoMlBAddedServices

/**
 * Dependency injection module that overrides Langium default services and contributes the
 * declared custom services. The Langium defaults can be partially specified to override only
 * selected services, while the custom services must be fully specified.
 */
export const ArduinoMlBModule: Module<ArduinoMlBServices, PartialLangiumServices & ArduinoMlBAddedServices> = {
    validation: {
        ArduinoMlBValidator: () => new ArduinoMlBValidator()
    }
};

/**
 * Create the full set of services required by Langium.
 *
 * First inject the shared services by merging two modules:
 *  - Langium default shared services
 *  - Services generated by langium-cli
 *
 * Then inject the language-specific services by merging three modules:
 *  - Langium default language-specific services
 *  - Services generated by langium-cli
 *  - Services specified in this file
 *
 * @param context Optional module context with the LSP connection
 * @returns An object wrapping the shared services and the language-specific services
 */
export function createArduinoMlBServices(context: DefaultSharedModuleContext): {
    shared: LangiumSharedServices,
    ArduinoMlB: ArduinoMlBServices
} {
    const shared = inject(
        createDefaultSharedModule(context),
        ArduinoMlBGeneratedSharedModule
    );
    const ArduinoMlB = inject(
        createDefaultModule({ shared }),
        ArduinoMlBGeneratedModule,
        ArduinoMlBModule
    );
    shared.ServiceRegistry.register(ArduinoMlB);
    registerValidationChecks(ArduinoMlB);
    return { shared, ArduinoMlB };
}
